function getOrderCredits(shipDate) {
  var url = 'http://api4dev.shopvisible.com/OrderService.asmx/GetOrderCredits?'+
  	'ClientName=TheMet_Staging&Guid=cd792d11-bf8c-e611-8101-06a33a6db6fd';
  var con = new java.net.URL(url).openConnection();
  con.requestMethod = 'GET';
  con.contentType = 'text/xml';
  var inReader = new java.io.BufferedReader(new java.io.InputStreamReader(con.inputStream));
  var response = new java.lang.StringBuffer();
  var inputLine;

  while ((inputLine = inReader.readLine()) != null) {
  	response.append(inputLine);
  }
  inReader.close();
  var retMap = new java.util.HashMap();

  var factory = javax.xml.parsers.DocumentBuilderFactory.newInstance();
  var builder = factory.newDocumentBuilder();
  var doc = builder.parse(new java.io.ByteArrayInputStream(response.toString().getBytes()));
  var xPathFactory = javax.xml.xpath.XPathFactory.newInstance();
  var xpath = xPathFactory.newXPath();
  var xpathExpression = xpath.compile('/OrderCredits/OrderCredit');
  var nodeList = xpathExpression.evaluate(doc, javax.xml.xpath.XPathConstants.NODESET);
  for (var i=0;i<nodeList.getLength();i++) {
    var orderCreditNode = nodeList.item(i);
    if (orderCreditNode.getNodeType() == org.w3c.dom.Node.ELEMENT_NODE) {
      var creditDate = orderCreditNode.getElementsByTagName('CreditDate').item(0).getTextContent();
	    var parts = creditDate.split('T');
	    if (parts.length > 1) {
		  creditDate = parts[0].replaceAll('\\-', '');
		  if (creditDate.equalsIgnoreCase(shipDate)) {
	      var creditType = orderCreditNode.getElementsByTagName('CreditType').item(0).getTextContent();
    	  var creditAmount = orderCreditNode.getElementsByTagName('CreditAmount').item(0).getTextContent();
      	  var creditReason = orderCreditNode.getElementsByTagName('CreditReason').item(0).getTextContent();
      	  var orderNo = orderCreditNode.getElementsByTagName('OrderNumber').item(0).getTextContent();
      	  var itemId = orderCreditNode.getElementsByTagName('ItemID').item(0).getTextContent();

      	  if ('S'.equalsIgnoreCase(creditType)) {
      	    creditReason = 'F';
      	  } else {
      	    creditReason = creditReasonMap.get(creditReason);
      	  } 
      	  var orderCredit = {
      	    "CreditType": creditType,
      	    "CreditAmount": creditAmount,
      	    "CreditReason": creditReason,
      	    "ItemId": itemId
      	  };	      
	      if (retMap.containsKey(orderNo)) {
	        var list = retMap.get(orderNo);
	        list.add(orderCredit);
	      } else {
	        var list = new java.util.ArrayList();
	        list.add(orderCredit);
	        retMap.put(orderNo, list);
	      }
	    }
      }
    }
  }
  return retMap;
}
